import { prisma } from "@/lib/db/prisma";

async function fixCache\(\) {
  const cache = await prisma.permissionCache.findUnique\({
    where: { userId: "cmlf6p4mo00009l3jo81imqki" }
  }\);

  if \(!cache\) {
    console.log\("❌ No permission cache found. Creating cache..."\);

    const admin = await prisma.user.findUnique\({
      where: { email: "admin@example.com" },
      include: {
        role: {
          include: {
            permissions: {
              include: {
                permission: true
              }
            }
          }
        }
      }
    }\);

    if \(admin && admin.role\) {
      const permissions = admin.role.permissions.map\(rp => rp.permission.name\);

      await prisma.permissionCache.create\({
        data: {
          userId: admin.id,
          permissions: permissions,
        }
      }\);

      console.log\("✅ Permission cache created with", permissions.length, "permissions"\);
    }
  } else {
    console.log\("✅ Permission cache exists with", cache.permissions.length, "permissions"\);
    console.log\("Last updated:", cache.updatedAt\);
  }
}

fixCache\(\)
  .then\(\(\) => process.exit\(0\)\)
  .catch\(console.error\);
EOF)
